;(function($){$.fn.bounce=function(options){var settings={bounceHeight:'20px',dropDownSpeed:300,delay:400};if(options){$.extend(settings,options)}return this.each(function(){var $this=$(this),itemheight=$this.outerHeight(true);$this.wrap('<div class="bounce"/>');$('.bounce').css({height:itemheight,position:'relative'});$this.hide();$this.animate({top:"-"+itemheight},function(){$(this).css({position:'relative'}).show()});$this.delay(settings.delay).animate({top:settings.bounceHeight},settings.dropDownSpeed,function(){$this.animate({top:0})})})}})(jQuery);
